import React from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './ShowAvailableBooks.module.css';

export default function ShowResults({ booksData }) {
    const navigate = useNavigate();

    const handleSelectBook = (id) => {
        navigate(`/book/${id}`);
    };

    return (
        // <section className="books-category">
        //     {booksData && booksData.length > 0 ? (
        //         <ul className="books">
        //             {booksData.map((book) => (
        //                 <li key={book.id} className="book-item">
        //                     {book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail ? (
        //                         <button onClick={() => handleSelectBook(book.id)}>
        //                             <img
        //                                 src={book.volumeInfo.imageLinks.thumbnail}
        //                                 alt={book.volumeInfo.title}
        //                             />
        //                             <p className='bookName'>{book.volumeInfo.title}</p>
        //                         </button>
        //                     ) : (
        //                         <p>No image available</p>
        //                     )}
        //                 </li>
        //             ))}
        //         </ul>
        //     ) : (
        //         <p>No results found.</p>
        //     )}
        // </section>

        <section className={classes.bookscategory}>
            {booksData && booksData.length > 0 ? (
                <ul className={classes.books}>
                    {booksData.map((book, index) => {
                        const title = book.title || 'No Title';
                        if (title === 'No Title') {
                            return null;
                        }

                        return (
                            <li key={`${book.id}-${index}`} className={classes.bookitem}>
                                {book.imageLinks && book.imageLinks.thumbnail ? (
                                    <button onClick={() => handleSelectBook(book.id)}>
                                        {isLoading ? (
                                            <div className={classes.skeletoncard}>
                                                <div className={classes.skeletonimage}></div>
                                                <div className={classes.skeletontitle}></div>
                                            </div>
                                        ) : (
                                            <>
                                                <img
                                                    src={book.imageLinks.thumbnail}
                                                    alt={`Book ${index + 1}`}
                                                    ref={index === booksData.length - 1 ? lastImageRef : null}
                                                />
                                                <p className={classes.booktitle}>{title}</p>
                                            </>
                                        )}
                                    </button>
                                ) : (
                                    <p>No image available</p>
                                )}
                            </li>
                        );
                    })}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
        </section>
    );
}
