import React, { useState, useContext } from 'react';
import { Form, NavLink, useRouteLoaderData } from 'react-router-dom';
import Modal from './Modal';
import SearchLogic from './SearchLogic'; // Import the SearchLogic component
import BookContext from '../contexts/BookContext';
import './MainNavigation.module.css';

export default function Header() {
  const token = useRouteLoaderData('root');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const { purchasedBooks, incrementBook, decrementBook } = useContext(BookContext);
  const [query, setQuery] = useState('');

  const toggleMenu = () => {
    setIsMenuOpen(prevState => !prevState);
  };

  const handleFetchPurchasedBooks = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <header>
      <h1>Design Books</h1>
      <div>
        <button className="menu-toggle" onClick={toggleMenu}>
          ☰
        </button>
        <nav className={`dropdown-menu ${isMenuOpen ? 'active' : ''}`}>
          <ul>
            <li>
              <NavLink id='text-link' to="/">Home</NavLink>
            </li>
            <li>
              {/* Use SearchLogic component and pass the necessary props */}
              <SearchLogic query={query} onInputChange={handleInputChange} />
            </li>
            {token && (
              <li>
                <button onClick={handleFetchPurchasedBooks}>
                  <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                    <path fill="#FFD700" d="M7 18c-1.1 0-1.99.9-1.99 2S5.9 22 7 22s2-.9 2-2s-.9-2-2-2M1 2v2h2l3.6 7.59l-1.35 2.45c-.16.28-.25.61-.25.96c0 1.1.9 2 2 2h12v-2H7.42c-.14 0-.25-.11-.25-.25l.03-.12l.9-1.63h7.45c.75 0 1.41-.41 1.75-1.03l3.58-6.49A1.003 1.003 0 0 0 20 4H5.21l-.94-2zm16 16c-1.1 0-1.99.9-1.99 2s.89 2 1.99 2s2-.9 2-2s-.9-2-2-2" />
                  </svg>
                </button>
              </li>
            )}
            {!token && (
              <li>
                <NavLink to="/auth?mode=login" id='text-link'>
                  Login
                </NavLink>
              </li>
            )}
            {token && (
              <li>
                <Form action="/logout" method="post">
                  <button>Logout</button>
                </Form>
              </li>
            )}
          </ul>
        </nav>
      </div>
      <Modal open={showModal} onClose={closeModal}>
        <div className='modal-header'>
          <h2>Purchased Books</h2>
          <button className="modal-close-button" onClick={closeModal}>
            <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
              <path fill="#9b872d" fillRule="evenodd" d="M6.793 6.793a1 1 0 0 1 1.414 0L12 10.586l3.793-3.793a1 1 0 1 1 1.414 1.414L13.414 12l3.793 3.793a1 1 0 0 1-1.414 1.414L12 13.414l-3.793 3.793a1 1 0 0 1-1.414-1.414L10.586 12L6.793 8.207a1 1 0 0 1 0-1.414" clipRule="evenodd" />
            </svg>
          </button>
        </div>
        <ul>
          {purchasedBooks.length > 0 ? (
            purchasedBooks.map((book, index) => (
              <li key={index}>
                <div className='purch-data'>
                  <img src={book.volumeInfo.imageLinks?.thumbnail} alt={book.volumeInfo.title} className="book-image" />
                  <p>{book.volumeInfo.title}</p>
                  <button onClick={() => decrementBook(book.id)}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                      <path fill="#9b872d" d="M5 11h14v2H5z" />
                    </svg>
                  </button>
                  <span>{book.quantity}</span>
                  <button onClick={() => incrementBook(book.id)}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                      <path fill="#9b872d" d="M18 12.998h-5v5a1 1 0 0 1-2 0v-5H6a1 1 0 0 1 0-2h5v-5a1 1 0 0 1 2 0v5h5a1 1 0 0 1 0 2" />
                    </svg>
                  </button>
                </div>
              </li>
            ))
          ) : (
            <p>No books purchased yet.</p>
          )}
        </ul>
      </Modal>
    </header>
  );
}
