import React from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './ShowAvailableBooks.module.css';
export default function SearchBar({ query, isDropdownVisible, searchResults, onInputChange, onSearch, onBookSelect }) {
    const navigate = useNavigate();

    const handleSelectBook = (id) => {
        navigate(`/book/${id}`);
    };

    return (
        <div className="search-box">
            <button className="btn-search" onClick={onSearch}>
                <i className="fas fa-search"></i>
            </button>
            <input
                type="text"
                className="input-search"
                placeholder="Type to Search..."
                value={query}
                onChange={onInputChange}
                onKeyPress={(e) => e.key === 'Enter' && onSearch()}
            />
            {/* {isDropdownVisible && searchResults.length > 0 && (
                <div className="dropdown-list">
                    <ul>
                        {searchResults.map((book, index) => (
                            <li key={index} className="dropdown-item" onClick={() => onBookSelect(book)}>
                                {book.volumeInfo.title} by {book.volumeInfo.authors ? book.volumeInfo.authors.join(', ') : 'Unknown'}
                            </li>
                        ))}
                    </ul>
                </div>
            )} */}


           
        </div>
    );
}


<section className="dropdown-list">
{searchResults && searchResults.length > 0 ? (
    <ul >
        {searchResults.map((book, index) => {
            const title = book.title || 'No Title';
            if (title === 'No Title') {
                return null;
            }
            return (
                <li key={`${book.id}-${index}`} >
                    {book.imageLinks && book.imageLinks.thumbnail ? (
                        <button onClick={() => handleSelectBook(book.id)}>
                            <img
                                src={book.imageLinks.thumbnail}
                                alt={`Book ${index + 1}`}
                            />
                            <p >{title}</p>
                        </button>
                    ) : (
                        <p>No image available</p>
                    )}
                </li>
            );
        })}
    </ul>
) : (
    <p>Loading...</p>
)}
</section>
