import {
  Form,
  Link,
  // useSearchParams,
  // useActionData,
  // useNavigation,
} from 'react-router-dom';
import { useInput } from '../../hooks/useInput.js';
import { isEmail, isNotEmpty, hasMinLength } from '../services/validation.js';
import Input from './Input.js';

import classes from './AuthForm.module.css';

function AuthForm() {
  // const navigate = useNavigate();

  // function navigateHandler() {
  //   navigate('/signup');
  // }


  const { value: emailValue,
    handleInputChange: handelEmailChange,
    handleInputBlur: handleEmailBlur,
    hasError: emailHasError
  } = useInput('', (value) => isEmail(value) && isNotEmpty(value));


  const { value: passwordValue,
    handleInputBlur: handlePasswordBlur,
    handleInputChange: handlePasswordChange,
    hasError: passwordHasError
  } = useInput('', (value) => hasMinLength(value, 6))
  const handleSubmit = (event) => {
    event.preventDefault();

    if (emailHasError || passwordHasError) {
      return;
    }
    console.log(emailValue, passwordValue);
  };
  const handleLogin = (event) => {
    event.preventDefault();
  };
  return (
    <>
      <Form method="post" className={classes.form}>
        <h2>Login</h2>

        <div className={classes.controlrow}>
          <div className={classes.control}>
            <Input
              label="Email"
              id="email"
              type="email"
              name="email"
              onBlur={handleEmailBlur}
              error={emailHasError && 'Please enter a valid email.'}
              onChange={handelEmailChange}
              value={emailValue}

            />
          </div>

          <div className={classes.control}>
            <Input
              label="Password"
              id="password"
              type="password"
              name="password"
              onChange={handlePasswordChange}
              onBlur={handlePasswordBlur}
              value={passwordValue}
              error={passwordHasError && 'Please enter vaild password.'}

            />
          </div>
        </div>

        <p className={classes.formactions}>

          <Link
            className={classes.buttonflat}
            to={'/signup'}>Signup</Link>
          <button onClick={handleLogin}
            className={classes.button}
          >Login</button>
        </p>
      </Form>
    </>
  );
}

export default AuthForm;
