import React, { useState, } from 'react';
// import { useNavigate } from 'react-router-dom';
import Modal from '../Modal/Modal.js';
import BookContext from '../../contexts/BookContext';
// import { useUser } from '../../contexts/UserContext';

export default function BookInfo({ book }) {
    // const navigate = useNavigate();
    const [showModal, setShowModal] = useState(false);
    const [rating, setRating] = useState(book.volumeInfo.averageRating || 0);
    const [hover, setHover] = useState(book.volumeInfo.averageRating || 0);
    // const [isLoading, setIsLoading] = useState(false);
    // const { isBuying, buyBook } = useContext(BookContext);
    // const { isLoggedIn } = useUser();

    const handleBuyBook = () => {
        setIsLoading(true);
        setTimeout(() => {
            // buyBook(book);
            setShowModal(false);
            setIsLoading(false);
        }, 5000);
    };

    const handleRatingChange = (value) => {
        setRating(value);
    };

    const openModal = () => {
        setShowModal(true);
    };

    const closeModal = () => {
        setShowModal(false);
    };

    const { volumeInfo } = book;
    const { title, description, authors, pageCount, imageLinks } = volumeInfo;

    // if (!isLoggedIn) {
    //     navigate('/signup');
    //     return null;
    // }

    return (
        <section className='book-detail'>
            <div className='book-info'>
                {imageLinks && imageLinks.thumbnail && (
                    <img src={imageLinks.thumbnail} alt={title} />
                )}
                <div className='book-data'>
                    <h1>{title}</h1>
                    <p>Author: {authors ? authors.join(', ') : 'Unknown'}</p>
                    <p>Page Count: {pageCount}</p>

                    <p>Rate this book:</p>
                    <div className="star-rating">
                        {[...Array(5)].map((star, index) => {
                            index += 1;
                            return (
                                <label key={index}>
                                    <input
                                        type="radio"
                                        name="rating"
                                        value={index}
                                        onClick={() => handleRatingChange(index)}
                                    />
                                    <i
                                        className={index <= (hover || rating) ? "fas fa-star" : "far fa-star"}
                                        onMouseEnter={() => setHover(index)}
                                        onMouseLeave={() => setHover(rating)}
                                    ></i>
                                </label>
                            );
                        })}
                    </div>
                    <div className='book-detail-button'>
                        <button onClick={openModal} className='book-button-buy'>Buy</button>

                        <input type="checkbox" id="like-checkbox" className="like-checkbox" />
                        <label htmlFor="like-checkbox" className="button-like">
                            <i className="fa fa-heart"></i>
                            <span>Like</span>
                        </label>
                    </div>
                </div>
            </div>
            <div className='book-description'>
                <p>{description}</p>
            </div>

            <Modal open={showModal} onClose={closeModal}>
                <div className="modal-content">
                    <div className="modal-header">
                        <h2>Confirm Purchase</h2>
                    </div>
                    <div className="modal-body">
                        <p>Are you sure you want to buy this book?</p>
                    </div>
                    {/* <div className="modal-footer">
                        <button onClick={handleBuyBook} disabled={isLoading || isBuying}>
                            {isLoading ? <div className="spinner"></div> : <p>Yes</p>}
                        </button>
                        <button onClick={closeModal}><p>No</p></button>
                    </div> */}
                </div>
            </Modal>
        </section>
    );
}
