import { useState } from 'react';
import { Form, useNavigation, useActionData } from 'react-router-dom';
import { useInput } from '../hooks/useInput.js';
import { isEmail, isNotEmpty, hasMinLength } from '../services/validation.js';

import Input from './Input.js';
import './signup.module.css';

export default function Signup() {
    const data = useActionData();
    const navigation = useNavigation();

    // const [searchParams] = useSearchParams();
    // const isLogin = searchParams.get('mode') === 'login';
    const isSubmitting = navigation.state === 'submitting';
    const { value: emailValue,
        handleInputChange: handelEmailChange,
        handleInputBlur: handleEmailBlur,
        hasError: emailHasError
    } = useInput('', (value) => isEmail(value) && isNotEmpty(value));


    const { value: firstNameValue,
        handleInputChange: handelFirstNameChange,
        handleInputBlur: handleFirstNameBlur,
        hasError: firstNameHasError
    } = useInput('', (value) => isNotEmpty(value));


    const { value: lastNameValue,
        handleInputChange: handelLastNameChange,
        handleInputBlur: handleLastNameBlur,
        hasError: lastNameHasError
    } = useInput('', (value) => isNotEmpty(value));

    const { value: passwordValue,
        handleInputBlur: handlePasswordBlur,
        handleInputChange: handlePasswordChange,
        hasError: passwordHasError
    } = useInput('', (value) => hasMinLength(value, 6));

    const [passwordsAreNotEqual, setPasswordsAreNotEqual] = useState(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        const fd = new FormData(event.target);
        const acquisitionChannel = fd.getAll('acquisition');
        const data = Object.fromEntries(fd.entries());
        data.acquisition = acquisitionChannel;
        if (data.password !== data['confirm-password']) {
            setPasswordsAreNotEqual(true);
            return;
        }
        console.log(data);
        console.log(acquisitionChannel);
        event.target.reset();

    };

    return (

        <Form method="post" onSubmit={handleSubmit} >
            <h2>Welcome on board!</h2>
            <p>We just need a little bit of data from you to get you started 🚀</p>

            <div className="control">
                <Input
                    label="Email"
                    id="email"
                    type="email"
                    name="email"
                    onBlur={handleEmailBlur}
                    error={emailHasError && 'Please enter a valid email.'}
                    onChange={handelEmailChange}
                    value={emailValue}

                />
            </div>

            <div className="control-row">
                <div className="control">
                    <Input
                        label="Password"
                        id="password"
                        type="password"
                        name="password"
                        onChange={handlePasswordChange}
                        onBlur={handlePasswordBlur}
                        value={passwordValue}
                        error={passwordHasError && 'Please enter vaild password.'}

                    />
                </div>

                <div className="control">
                    <Input
                        label="Confirm Password"
                        id="confirm-password"
                        type="password"
                        name="confirm-password"

                    />
                    <div className='control-error'>{passwordsAreNotEqual && <p>Passwords must match.</p>}</div>
                </div>
            </div>

            <hr />

            <div className="control-row">
                <div className="control">
                    <Input
                        label="First Name"
                        type="text"
                        id="first-name"
                        name="first-name"
                        onBlur={handleFirstNameBlur}
                        error={lastNameHasError && 'Please enter a valid last Name.'}
                        onChange={handelFirstNameChange}
                        value={firstNameValue} />
                </div>

                <div className="control">
                    <Input
                        label="Last Name"
                        type="text"
                        id="last-name"
                        name="last-name"
                        onBlur={handleLastNameBlur}
                        error={firstNameHasError && 'Please enter a valid first Name.'}
                        onChange={handelLastNameChange}
                        value={lastNameValue} />
                </div>
            </div>

            <div className="control">
                <label htmlFor="phone">What best describes your role?</label>
                <select id="role" name="role">
                    <option value="student">Student</option>
                    <option value="teacher">Teacher</option>
                    <option value="employee">Employee</option>
                    <option value="founder">Founder</option>
                    <option value="other">Other</option>
                </select>
            </div>

            <fieldset>
                <legend>How did you find us?</legend>
                <div className="control">
                    <input
                        type="checkbox"
                        id="google"
                        name="acquisition"
                        value="google"
                    />
                    <label htmlFor="google">Google</label>
                </div>

                <div className="control">
                    <input
                        type="checkbox"
                        id="friend"
                        name="acquisition"
                        value="friend"
                    />
                    <label htmlFor="friend">Referred by friend</label>
                </div>

                <div className="control">
                    <input type="checkbox" id="other" name="acquisition" value="other" />
                    <label htmlFor="other">Other</label>
                </div>
            </fieldset>

            <div className="control">
                <label htmlFor="terms-and-conditions">
                    <input type="checkbox" id="terms-and-conditions" name="terms" />I
                    agree to the terms and conditions
                </label>
            </div>

            <p className="form-actions">
                <button type="reset" className="button button-flat">
                    Reset
                </button>
                <button
                    className="button"
                    disabled={isSubmitting}>
                    {isSubmitting ? 'Submitting...' : 'Save'}
                </button>
            </p>
        </Form>
    )
}
