// import { Navigate } from 'react-router-dom';
// import { getAuthToken } from '../util/auth';

// const PrivateRoute = ({ element }) => {
//     const token = getAuthToken();

//     return token ? element : <Navigate to="/auth" />;
// };

// export default PrivateRoute;

import { useEffect, useState } from 'react';
import Modal from '../components/Modal.js';
import { getAuthToken } from '../util/auth';

const PrivateRoute = ({ element }) => {
    const [showModal, setShowModal] = useState(false);
    const token = getAuthToken();

    useEffect(() => {
        if (!token) {
            setShowModal(true);
        }
    }, [token]);

    const closeModal = () => {
        setShowModal(false);
    };

    return token ? element : (
        <Modal open={showModal} onClose={closeModal}>
            <p className='modal-header'>You need to be logged in to view this page.</p>
            <button className="modal-close-button" onClick={closeModal}>
                <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                    <path fill="#9b872d" fill-rule="evenodd" d="M6.793 6.793a1 1 0 0 1 1.414 0L12 10.586l3.793-3.793a1 1 0 1 1 1.414 1.414L13.414 12l3.793 3.793a1 1 0 0 1-1.414 1.414L12 13.414l-3.793 3.793a1 1 0 0 1-1.414-1.414L10.586 12L6.793 8.207a1 1 0 0 1 0-1.414" clip-rule="evenodd" />
                </svg>
            </button>
        </Modal>

    );
};


export default PrivateRoute;
