import React from 'react';
import styles from './ShowResults.module.css';

export default function ShowResults({ booksData = [], onBookSelect }) {
    console.log('ShowResults received booksData:', booksData);

    return (
        <div className={styles.searchResults}>
            {booksData.length > 0 ? (
                booksData.map((book, index) => (
                    <div key={index} className={styles.searchResult} onClick={() => onBookSelect(book)}>
                        <img src={book.volumeInfo.imageLinks?.thumbnail} alt={book.volumeInfo.title} />
                        <div>
                            <h3>{book.volumeInfo.title}</h3>
                            <p>{book.volumeInfo.authors?.join(', ')}</p>
                        </div>
                    </div>
                ))
            ) : (
                <p>No results found.</p>
            )}
        </div>
    );
}
