import React, { useState } from 'react';
import { Form, NavLink, useRouteLoaderData } from 'react-router-dom';

import classes from './MainNavigation.module.css';

export default function Header() {
  const token = useRouteLoaderData('root');
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  const toggleMenu = () => {
    setIsMenuOpen((prevState) => !prevState);
  };

  return (
    <>

      {/* <header>
        <div >
          <button className={classes.menutoggle} onClick={toggleMenu}>
            <svg xmlns="http://www.w3.org/2000/svg" width="2.34rem" height="2rem" viewBox="0 0 28 24"><path fill="#FFD700" d="M2.61 0h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.39h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.391h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22" /></svg>
          </button>

          <nav className={`${classes.dropdownmenu} ${isMenuOpen ? classes.active : ''}`}>
            <ul>
              <li>
                <NavLink className={classes.textLink} to="/">Home</NavLink>
              </li>
              {!token && (
                <li>
                  <NavLink to="/auth?mode=login" className={classes.textLink}>Login</NavLink>
                </li>
              )}
              {token && (
                <li>
                  <NavLink to="/purchasedBooks" className={classes.textLink}>Purchased</NavLink>
                </li>
              )}
              {token && (
                <li>
                  <Form action="/logout" method="post">
                    <button className={classes.textLink}>Logout</button>
                  </Form>
                </li>
              )}
            </ul>
          </nav>
        </div>
      </header> */}
      <header>
        <div className={`container ${isMenuOpen ? 'active' : ''}`}>
          <div className="sidebar" style={{ width: isMenuOpen ? '200px' : '50px' }}>
            <div className="top_section">
              <h1 className="logo" style={{ display: isMenuOpen ? 'block' : 'none' }}>Logo</h1>
              <div className="bars" onClick={toggleMenu}>
                <svg xmlns="http://www.w3.org/2000/svg" width="2.34rem" height="2rem" viewBox="0 0 28 24">
                  <path fill="#FFD700" d="M2.61 0h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.39h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.391h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22" />
                </svg>
              </div>
            </div>
            <nav className={`${classes.dropdownmenu} ${isMenuOpen ? classes.active : ''}`}>
              <ul>
                <li>
                  <NavLink className={classes.textLink} to="/"><svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 16 16"><path fill="#FFD700" d="M8.687 1.262a1 1 0 0 0-1.374 0L2.469 5.84A1.5 1.5 0 0 0 2 6.931v5.57A1.5 1.5 0 0 0 3.5 14H5a1.5 1.5 0 0 0 1.5-1.5V10a.5.5 0 0 1 .5-.5h2a.5.5 0 0 1 .5.5v2.5A1.5 1.5 0 0 0 11 14h1.5a1.5 1.5 0 0 0 1.5-1.5V6.93a1.5 1.5 0 0 0-.47-1.09z" /></svg>Home</NavLink>
                </li>
                {!token && (
                  <li>
                    <NavLink to="/auth?mode=login" className={classes.textLink}><svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24"><path fill="#FFD700" d="M10 11H2.048c.502-5.053 4.765-9 9.95-9c5.523 0 10 4.477 10 10s-4.477 10-10 10c-5.185 0-9.448-3.947-9.95-9h7.95v3l5-4l-5-4z" /></svg>Login</NavLink>
                  </li>
                )}
                {token && (
                  <li>
                    <NavLink to="/purchasedBooks" className={classes.textLink}>Purchased</NavLink>
                  </li>
                )}
                {token && (
                  <li>
                    <Form action="/logout" method="post">
                      <button className={classes.textLink}>Logout</button>
                    </Form>
                  </li>
                )}
              </ul>
            </nav>
          </div>
        </div>
      </header>

    </>
  );
}
