import React, { useState, useEffect, useRef, useCallback } from 'react';
import ShowAvailableBooks from './ShowAvailableBooks.js';
import { fetchAvailableBooks } from '../services/api-call.js';
import './Spinner.module.css'; // Make sure the CSS file is properly imported

export default function BookImage() {
    const [booksData, setBooksData] = useState([]);
    const [page, setPage] = useState(1);
    const [isLoading, setIsLoading] = useState(false);
    const observer = useRef(null);

    useEffect(() => {
        const fetchBookData = async () => {
            setIsLoading(true); // Set loading state to true before fetching
            try {
                const resData = await fetchAvailableBooks(page);
                const newBooksData = resData.items.map(item => ({
                    id: item.id,
                    imageLinks: item.volumeInfo.imageLinks,
                    title: item.volumeInfo.title
                }));
                setBooksData(prevData => [...prevData, ...newBooksData]);
            } catch (error) {
                console.error('Error fetching book data:', error.message);
            } finally {
                setIsLoading(false); // Set loading state to false after fetching
            }
        };

        fetchBookData(); 
    }, [page]); 

    const lastImageRef = useCallback(node => {
        if (isLoading) return; 
        if (observer.current) observer.current.disconnect();

        observer.current = new IntersectionObserver(entries => {
            if (entries[0].isIntersecting) {
                setPage(prevPage => prevPage + 1); 
            }
        });

        if (node) observer.current.observe(node); 
    }, [isLoading]); 

    return (
        <div>
            <ShowAvailableBooks booksData={booksData} lastImageRef={lastImageRef} isLoading={isLoading} />
            {isLoading && (
                <div className="spinner" style={{ textAlign: 'center', margin: '20px 0' }}>
                    <div className="double-bounce1"></div>
                    <div className="double-bounce2"></div>
                </div>
            )}
        </div>
    );
}
