import { useNavigate } from 'react-router-dom';


export default function ShowAvailableBooks({ booksData, lastImageRef }) {
    const navigate = useNavigate();

    function handleSelectBook(id) {
        navigate(`/book/${id}`);
    }

    return (

 
        <section className="books-category">
            {booksData && booksData.length > 0 ? (
                <ul className="books">
                    {booksData.map((book, index) => {
                        const title = book.title || 'No Title';
                        if (title === 'No Title') {
                            return null; 
                        }

                        return (
                            <li key={`${book.id}-${index}`} className="book-item">
                                {book.imageLinks && book.imageLinks.thumbnail ? (
                                    <button onClick={() => handleSelectBook(book.id)}>
                                        <img
                                            src={book.imageLinks.thumbnail}
                                            alt={`Book ${index + 1}`}
                                            ref={index === booksData.length - 1 ? lastImageRef : null}
                                        />
                                        <p className="book-title">{title}</p>
                                    </button>
                                ) : (
                                    <p>No image available</p>
                                )}
                            </li>
                        );
                    })}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
        </section>


    );
}
