import React from 'react';
import styles from './SearchBar.module.css';

export default function SearchBar({ query, onInputChange, onSearch }) {
    return (
        <div className={styles.searchBar}>
            <input
                type="text"
                value={query}
                onChange={onInputChange}
                placeholder="Search for books..."
            />
            <button onClick={onSearch}>
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                    <path fill="#fff" d="M15.5 14h-.79l-.28-.27a6.518 6.518 0 0 0 1.48-5.34C15.34 5.61 13.03 3.5 10 3.5S4.66 5.61 4.07 8.39a6.518 6.518 0 0 0 1.48 5.34l-.28.27h-.79l-4.99 5L3 21l5-4.99v-.79l.27-.28a6.518 6.518 0 0 0 5.34 1.48C16.39 17.34 18.5 15.03 18.5 12s-2.11-5.34-5.02-5.93c-2.11-.41-4.26.66-5.34 2.39C6.29 9.84 6 10.91 6 12s.29 2.16.93 3.07c1.1 1.11 2.66 1.66 4.13 1.6h.06c1.47-.05 2.83-.61 3.87-1.6z" />
                </svg>
            </button>
        </div>
    );
}
