import React from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './Search.module.css';

export default function SearchBar({ query, isDropdownVisible, searchResults, onInputChange, onSearch, onBookSelect }) {
    const navigate = useNavigate();

    const handleSelectBook = (id) => {
        navigate(`/book/${id}`);
    };


    return (
        <div className={classes.searchbox}>
            <button className={classes.btnsearch} onClick={onSearch}>
                <i className="fas fa-search"></i>
            </button>
            <input
                type="text"
                className={classes.inputsearch}
                placeholder="Type to Search..."
                value={query}
                onChange={onInputChange}
                onKeyPress={(e) => e.key === 'Enter' && onSearch()}
            />
            {/* {isDropdownVisible && searchResults.length > 0 && (
                <div className={classes.bookscategory}>
                    <ul className={classes.books}>
                        {searchResults.map((book, index) => (
                            <li key={index} onClick={() => onBookSelect(book)} className={classes.bookitem}>
                                <button
                                    onClick={() => handleSelectBook(book.id)}
                                >
                                    <p className={classes.booktitle}>{book.volumeInfo.title}</p>
                                    <p className={classes.booktitle}>by {book.volumeInfo.authors ? book.volumeInfo.authors.join(', ') : 'Unknown'}</p>
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            )} */}
        </div>
    );
}
