import React, { useState } from 'react';
import { Form, NavLink, useRouteLoaderData } from 'react-router-dom';

import classes from './MainNavigation.module.css';

export default function Header() {
  const token = useRouteLoaderData('root');
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  const toggleMenu = () => {
    setIsMenuOpen((prevState) => !prevState);
  };

  return (
    <>

      {/* <header>
        <div >
          <button className={classes.menutoggle} onClick={toggleMenu}>
            <svg xmlns="http://www.w3.org/2000/svg" width="2.34rem" height="2rem" viewBox="0 0 28 24"><path fill="#FFD700" d="M2.61 0h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.39h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22m0 9.391h22.431a2.61 2.61 0 1 1 0 5.22H2.61a2.61 2.61 0 1 1 0-5.22" /></svg>
          </button>

          <nav className={`${classes.dropdownmenu} ${isMenuOpen ? classes.active : ''}`}>
            <ul>
              <li>
                <NavLink className={classes.textLink} to="/">Home</NavLink>
              </li>
              {!token && (
                <li>
                  <NavLink to="/auth?mode=login" className={classes.textLink}>Login</NavLink>
                </li>
              )}
              {token && (
                <li>
                  <NavLink to="/purchasedBooks" className={classes.textLink}>Purchased</NavLink>
                </li>
              )}
              {token && (
                <li>
                  <Form action="/logout" method="post">
                    <button className={classes.textLink}>Logout</button>
                  </Form>
                </li>
              )}
            </ul>
          </nav>
        </div>
      </header> */}
      <header>
        <div className={`${classes.sidebar} ${isMenuOpen ? classes.active : ''}`}>
          <div className={classes.top_section}>
            <div className={classes.logo}>Logo</div>
            <div className={classes.bars} onClick={toggleMenu}>
              {/* Icon for opening/closing menu */}
            </div>
          </div>
          <nav className={`${classes.dropdownmenu} ${isMenuOpen ? classes.active : ''}`}>
            <ul>
              <li>
                <NavLink className={classes.textLink} to="/">
                  <div className="nav-item">
                    <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 16 16">
                      <path fill="#FFD700" d="M8.687 1.262a1 1 0 0 0-1.374 0L2.469 5.84A1.5 1.5 0 0 0 2 6.931v5.57A1.5 1.5 0 0 0 3.5 14H5a1.5 1.5 0 0 0 1.5-1.5V10a.5.5 0 0 1 .5-.5h2a.5.5 0 0 1 .5.5v2.5A1.5 1.5 0 0 0 11 14h1.5a1.5 1.5 0 0 0 1.5-1.5V6.93a1.5 1.5 0 0 0-.47-1.09z" />
                    </svg>
                    {isMenuOpen && <span>Home</span>}
                  </div>
                </NavLink>
              </li>
              {!token && (
                <li>
                  <NavLink to="/auth?mode=login" className={classes.textLink}>
                    <div className="nav-item">
                      <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                        <path fill="#FFD700" fillRule="evenodd" d="M1.625 12c0 .414.336.75.75.75h10.973l-1.961 1.68a.75.75 0 1 0 .976 1.14l3.5-3a.75.75 0 0 0 0-1.14l-3.5-3a.75.75 0 1 0-.976 1.14l1.96 1.68H2.376a.75.75 0 0 0-.75.75" clipRule="evenodd" />
                        <path fill="#FFD700" d="M9.375 9.75h.378a2.25 2.25 0 0 1 3.586-2.458l3.5 3a2.25 2.25 0 0 1 0 3.416l-3.5 3a2.25 2.25 0 0 1-3.586-2.458h-.378V16c0 2.828 0 4.243.879 5.121c.878.879 2.293.879 5.121.879h1c2.828 0 4.243 0 5.121-.879c.879-.878.879-2.293.879-5.121V8c0-2.828 0-4.243-.879-5.121C20.618 2 19.203 2 16.375 2h-1c-2.828 0-4.243 0-5.121.879c-.879.878-.879 2.293-.879 5.121z" />
                      </svg>
                      {isMenuOpen && <span>Login</span>}
                    </div>
                  </NavLink>
                </li>
              )}
              {token && (
                <li>
                  <NavLink to="/purchasedBooks" className={classes.textLink}>
                    <div className="nav-item">
                      <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                        <path fill="#FFD700" fillRule="evenodd" d="M14.665 2.33a.75.75 0 0 1 1.006.335l2.201 4.402c1.353.104 2.202.37 2.75 1.047c.9 1.114.541 2.79-.177 6.143l-.429 2c-.487 2.273-.73 3.409-1.555 4.076c-.825.667-1.987.667-4.311.667h-4.3c-2.324 0-3.486 0-4.31-.667c-.826-.667-1.07-1.803-1.556-4.076l-.429-2c-.718-3.353-1.078-5.029-.177-6.143c.548-.678 1.397-.943 2.75-1.047l2.201-4.402a.75.75 0 0 1 1.342.67l-1.835 3.67C8.319 7 8.846 7 9.422 7h5.156c.576 0 1.103 0 1.586.005l-1.835-3.67a.75.75 0 0 1 .336-1.006M7.25 12a.75.75 0 0 1 .75-.75h8a.75.75 0 0 1 0 1.5H8a.75.75 0 0 1-.75-.75M10 14.25a.75.75 0 0 0 0 1.5h4a.75.75 0 0 0 0-1.5z" clipRule="evenodd" />
                      </svg>
                      {isMenuOpen && <span>Purchased</span>}
                    </div>
                  </NavLink>
                </li>
              )}
              {token && (
                <li>
                  <Form action="/logout" method="post">
                    <button className={classes.textLink}>
                      <div className="nav-item">
                        <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" viewBox="0 0 24 24">
                          <path fill="#FFD700" fillRule="evenodd" d="M16.125 12a.75.75 0 0 0-.75-.75H4.402l1.961-1.68a.75.75 0 1 0-.976-1.14l-3.5 3a.75.75 0 0 0 0 1.14l3.5 3a.75.75 0 1 0 .976-1.14l-1.96-1.68h10.972a.75.75 0 0 0 .75-.75" clipRule="evenodd" />
                          <path fill="#FFD700" d="M9.375 8c0 .702 0 1.053.169 1.306a1 1 0 0 0 .275.275c.253.169.604.169 1.306.169h4.25a2.25 2.25 0 0 1 0 4.5h-4.25c-.702 0-1.053 0-1.306.168a1 1 0 0 0-.275.276c-.169.253-.169.604-.169 1.306c0 2.828 0 4.243.879 5.121c.878.879 2.292.879 5.12.879h1c2.83 0 4.243 0 5.122-.879c.879-.878.879-2.293.879-5.121V8c0-2.828 0-4.243-.879-5.121C20.617 2 19.203 2 16.375 2h-1c-2.829 0-4.243 0-5.121.879c-.879.878-.879 2.293-.879 5.121" />
                        </svg>
                        {isMenuOpen && <span>Logout</span>}
                      </div>
                    </button>
                  </Form>
                </li>
              )}
            </ul>
          </nav>

        </div>

    </header >

    </>
  );
}
